Problem problem = new UF1();
NondominatedPopulation referenceSet = NondominatedPopulation.load("pf/UF1.pf");

Hypervolume hypervolume = new Hypervolume(problem, referenceSet);
IndicatorStatistics statistics = new IndicatorStatistics(hypervolume);

for (int seed = 0; seed < 10; seed++) {
    PRNG.setSeed(seed);

    NSGAII algorithm1 = new NSGAII(problem);
    algorithm1.run(10000);
    statistics.add("NSGA-II", algorithm1.getResult());

    MOEAD algorithm2 = new MOEAD(problem);
    algorithm2.run(10000);
    statistics.add("MOEA/D", algorithm2.getResult());

    OMOPSO algorithm3 = new OMOPSO(problem);
    algorithm3.run(10000);
    statistics.add("OMOPSO", algorithm3.getResult());
}

statistics.display();

Problem problem = new UF1();
NSGAII algorithm = new NSGAII(problem);

Instrumenter instrumenter = new Instrumenter()
        .withReferenceSet("pf/UF1.pf")
        .withFrequency(100)
        .attachGenerationalDistanceCollector();

InstrumentedAlgorithm<NSGAII> instrumentedAlgorithm =
        instrumenter.instrument(algorithm);

instrumentedAlgorithm.run(10000);

instrumentedAlgorithm.getObservations().display();

public static class Srinivas extends AbstractProblem {

    public Srinivas() {
        super(2, 2, 2);
    }

    @Override
    public void evaluate(Solution solution) {
        double x = EncodingUtils.getReal(solution.getVariable(0));
        double y = EncodingUtils.getReal(solution.getVariable(1));

        double f1 = Math.pow(x - 2.0, 2.0) + Math.pow(y - 1.0, 2.0) + 2.0;
        double f2 = 9.0*x - Math.pow(y - 1.0, 2.0);
        double c1 = Math.pow(x, 2.0) + Math.pow(y, 2.0);
        double c2 = x - 3.0*y;

        solution.setObjectiveValue(0, f1);
        solution.setObjectiveValue(1, f2);

        solution.setConstraintValue(0, c1);
        solution.setConstraintValue(1, c2);
    }

    @Override
    public Solution newSolution() {
        Solution solution = new Solution(2, 2, 2);

        solution.setVariable(0, new RealVariable(-20.0, 20.0));
        solution.setVariable(1, new RealVariable(-20.0, 20.0));

        solution.setConstraint(0, LessThanOrEqual.to(225.0));
        solution.setConstraint(1, LessThanOrEqual.to(-10.0));

        return solution;
    }

}

Problem problem = new Srinivas();

NSGAII algorithm = new NSGAII(problem);
algorithm.run(10000);

algorithm.getResult().display();
